        switch (i){

            case (i<5):
                position = 2 + (i-1)*6;
                bars+='<g fill=\"none\" stroke=\"green\" stroke-width=\"4\">' + '<path d=\"M 2 12 l 0 12\"/>' +'</g>';
                break;
            case (i===5):
                position = 2 + (i-1)*6;

                break;
            case (i>5):
                position = 2 + ( i-1)*6;

                break;
        }
    }

