import React, {useEffect, useState} from 'react';

import fetchStockDataIEX from "../../Utilities/Data/Stocks/StockDataIEX";



export default function DashTile(props) {
    // Declare a new state variable, which we'll call "count"
    const [counter,setCounter] = useState(0);
    const [price, setPrice] = useState(0);
    var negative = (false);


    useEffect(()=>{
       async function fetchData(){
           const request = await getPrice(props.symbol);
       }
       fetchData();


    },[counter]);


    return (
<div className="px-1 py-1 ">

            <div className="flex flex-row ">
                <div className="px-1 py-1 rounded-md border-2 { negative ? border-red-500 hover:bg-red-400 : border-green-500 hover:bg-green-400 } text-sm font-medium text-white bg-gray-900 focus:outline-none focus:text-white focus:bg-gray-700 ">

                    <div className="flex flex-row">
                        <div className=" flex-column">
                            <div className="text-left font-bold">
                                {props.symbol}
                            </div>
                            <div className="text-left font-bold">
                                {price}
                            </div>
                        </div>

                        <div className="p-1 text-right align-text-top font-bold">
                            {props.change}
                        </div>
                    </div>
                </div>

            </div>

            </div>




    );
}

function getPrice(ticker){
    return fetchStockDataIEX(ticker);

}
