import React, { useState } from 'react';

import Counter from "../Counter";
import DashTile from "./DashTile";
import AlertWidgetH from "./Widgets/AlertWidgetH";
import DashTileExpanded from "./DashTileExpanded";
import {fetchWatchlist} from "../../Utilities/Data/Watchlist/Watchlist";



export default function DashTileGroup(props) {
    // Declare a new state variable, which we'll call "count"
    const [expanded, setExpanded] = useState(false);

    const elements = fetchWatchlist();

    const stocks = []
    if (expanded) {
            for (const [index, value] of elements.entries()) {
                stocks.push(<DashTileExpanded symbol={value}/>)
            }
            } else {
                for (const [index, value] of elements.entries()) {
                    stocks.push(<DashTile symbol={value}/>)
                }
    }

    return (
                <div className="m-1 px-1 py-1 rounded-md border-2 border-green-500 text-sm font-medium text-white bg-TDBase focus:outline-none focus:text-white focus:bg-TDBase">
                    <div className="flex flex-row divide-x gap-x-2">

                        <div>
                            <div><div className="p-1 flex flex-row">
                                <button onClick={() => setExpanded((!expanded))}>
                                    <div className="flex flex-row">
                                        <div className="h-5 w-5">
                                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 9v3m0 0v3m0-3h3m-3 0H9m12 0a9 9 0 11-18 0 9 9 0 0118 0z" />
                                            </svg>
                                        </div>
                                        <div className="px-1 text-left font-bold">
                                            {
                                                props.title
                                            }
                                        </div>

                                    </div>

                                </button>

                            </div>
                                <div className="flex flex-row">
                                    <div className="p-1 text-left font-bold">
                                        <AlertWidgetH/>
                                    </div>
                                    <div className="p-1 text-left font-bold">
                                        +5%
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div>
                        <div className=" flex flex-row">
                            {stocks}

                        </div>
                    </div>
                    </div>
                </div>
    );
}
