import React, { useState,useEffect } from 'react';



export default function TableItem(props) {

    const [EditMode, setEditMode] = useState(false);
    const [rowData,UpdateRowData] = useState({"Symbol":props.name,"price":20,"open":20,"close":20})

    useEffect(() => {
        if(document.getElementById("symbolInput")) {
            var inputText = document.getElementById("symbolInput");
            inputText.addEventListener("keyup", function (event) {
                if (event.keyCode === 13) {
                    event.preventDefault();
                    if(inputText.value){
                        if(inputText.value!==rowData["Symbol"]){
                            rowData["price"] += 1;
                        }
                        rowData["Symbol"] = inputText.value
                    }
                    UpdateRowData(rowData);
                    setEditMode(false);
                }
            });
        }

    });

    return (
        <tr className ={(EditMode ? "bg-blue-300" : "bg-gray-300")}>
            <td className={"px-6 py-4 whitespace-no-wrap"}>
                <div className="flex items-center">
                    <div className="flex-shrink-0 h-10 w-10">
                        <img className="h-10 w-10 rounded-full"
                             src="https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-1.2.1&amp;ixid=eyJhcHBfaWQiOjEyMDd9&amp;auto=format&amp;fit=facearea&amp;facepad=4&amp;w=256&amp;h=256&amp;q=60"
                             alt=""/>
                    </div>
                    <div className="ml-4">
                        <div className="text-lg leading-5 font-bold text-gray-900">
                            {EditMode? <input id="symbolInput" placeholder={rowData['Symbol']}/> : <span onClick={() => setEditMode((true))}>{rowData['Symbol']}</span> }
                        </div>
                    </div>
                </div>
            </td>
            <td className="px-6 py-4 whitespace-no-wrap">
                <div className="text-left text-lg leading-5 font-bold text-gray-900">
                    {rowData['price']}
                </div>
            </td>
            <td className="px-6 py-4 whitespace-no-wrap">
                <div className="text-left text-lg leading-5 font-bold text-gray-900">
                    {rowData['open']}
                </div>
            </td>
            <td className="px-6 py-4 whitespace-no-wrap">
                <div className="text-left text-lg leading-5 font-bold text-gray-900">
                    {rowData['close']}
                </div>
            </td>
            <td className="px-6 py-4 whitespace-no-wrap">
                <div className="text-sm leading-5 text-gray-900">Regional Paradigm Technician</div>
                <div className="text-sm leading-5 text-gray-500">Optimization</div>
            </td>
            <td className="px-6 py-4 whitespace-no-wrap">
                <span
                    className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-200 text-green-800">
                  Active
                </span>
                <span
                    className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-red-200 text-green-800">
                  Runner
                </span>
                <span
                    className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-blue-200 text-green-800">
                  Imbalanced
                </span>
            </td>
            <td className="px-6 py-4 whitespace-no-wrap text-sm leading-5 text-gray-600">
                {EditMode? <input class=" mt-1 relative rounded-md shadow-sm form-input sm:text-sm " placeholder="Admin"/> : "Admin"}
            </td>
            <td className="px-6 py-4 whitespace-no-wrap text-right text-sm leading-5 font-medium">
                 <button onClick={() => setEditMode(true)}>
                    Edit
                </button>
                {EditMode? <span><button onClick={() => setEditMode((false))}>
                    save
                </button></span>: ""}
            </td>
        </tr>


    );
}

