import React, { useState } from 'react';

import Counter from "../Counter";
import DashTile from "./DashTile";
import AlertWidgetH from "./Widgets/AlertWidgetH";



export default function DashTileGroup(props) {
    // Declare a new state variable, which we'll call "count"
    const [expanded, setExpanded] = useState(false);

    const elements = [['AAPL', 12, 120], ['AAPL', -12, 120], ['AAPL', 0, 120], ['AAPL', 12, 120]];

    const stocks = []
    if (!expanded) {

    for (const [index, value] of elements.entries()) {
        if (value[1] <= 0) {
            var change = value[1] + "%";

        } else {
            var change = "+" + value[1] + "%";

        }

        stocks.push(<DashTile symbol={value[0]} change={change} price={value[2]}/>)
    }
} else {

        for (const [index, value] of elements.entries()) {
            if (value[1] <= 0) {
                var change = value[1] + "%";

            } else {
                var change = "+" + value[1] + "%";

            }

            stocks.push(<DashTile symbol={value[0]} change={change} price={value[2]}/>)
        }


}

    return (
                <div className="m-1 px-1 py-1 rounded-md border-2 border-green-500 text-sm font-medium text-white bg-gray-900 focus:outline-none focus:text-white focus:bg-gray-700">
                    <div className="flex flex-row divide-x gap-x-2">

                        <div>
                            <div>
                                <div className="p-1 text-left font-bold">
                                    {
                                        props.title
                                    }
                                </div>
                                <div className="flex flex-row">
                                    <div className="p-1 text-left font-bold">
                                        +5%
                                    </div>
                                    <div className="p-1 text-left font-bold">
                                        <AlertWidgetH/>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div>
                        <div className=" flex flex-row">
                            {stocks}

                        </div>
                    </div>
                    </div>
                </div>
    );
}
